package httpserver;import java.io.IOException;import java.nio.file.Path;public class HttpServerStarter {    private final Path webappDir;    private final int port;    public HttpServerStarter(Path webappDir, int port) {        this.webappDir = webappDir;        this.port = port;    }    public HttpServer start() throws IOException {        return new HttpServer(new HttpRequestHandler(webappDir), port);    }}